import React, { Component } from "react";
import { isAuthenticated } from "../auth";
import { Redirect, Link } from "react-router-dom";
import { read } from "./apiUser";
import DefaultProfile from "../assets/images/avatar.png";

const profileImage = {
  width: "40px",
  borderRadius: "100px",
  margin: "0"
};

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: null,
      redirectToSignin: false
    };
  }

  checkUser() {
    let user = this.state.user;

    console.log(user);
    // if (this.state.user) {
    //   console.log(this.state.user);
    // } else if (!this.state.user) {
    //   console.log("USER IS NULL.");
    // }
  }

  init = userId => {
    const token = isAuthenticated().token;

    read(userId, token).then(data => {
      if (data.error) {
        this.setState({ redirectToSignin: true });
        console.log("Error.");
      } else {
        this.setState({ user: data });
      }
    });
  };

  //Courtesy of Alex Zenin on july 10 2019
  shitcode = () => {
    return (
      isAuthenticated().user &&
      isAuthenticated().user._id == this.state.user._id && (
        <div className="d-inline-block mt-5 mb-5">
          <Link
            className="btn btn-raised btn-success mr-5"
            to={`/user/edit/${this.state.user._id}`}
          >
            Edit Profile
          </Link>
          <Link
            className="btn btn-raised btn-danger mr-5"
            to={`/user/edit/${this.state.user._id}`}
          >
            Delete
          </Link>
        </div>
      )
    );
  };

  componentDidMount() {
    const userId = this.props.match.params.userId;
    this.init(userId);
  }

  render() {
    const { redirectToSignin, user } = this.state;

    if (redirectToSignin) {
      return <Redirect to="/signin" />;
    }
    return (
      <div className="container mt-5 mb-5">
        <div className="row">
          <div className="col-md-6">
            <img
              style={profileImage}
              src={DefaultProfile}
              alt={user.username}
            />
            <p>this is the user profile, printing below is the username.</p>
            <h2>Handle: @{user ? user.username + " " : "loading"}</h2>
            <h6>Email: {user ? user.email + " " : "loading"}</h6>

            <p>
              {user ? user.name.firstName + " " : "loading"}
              {user ? user.name.lastName : "loading"}
            </p>
          </div>
          <div className="col-md-6" />
          {user ? this.shitcode() : "Loading..."}
        </div>
      </div>
    );
  }
}

export default Profile;
