import React, { Component } from "react";
import { isAuthenticated } from "../auth";
import { read } from "./apiUser";
import { Redirect, Link } from "react-router-dom";

class EditProfile extends Component {
  constructor() {
    super();
    this.state = {
      id: null,
      firstName: null,
      lastName: null,
      email: null,
      username: null,
      password: null
    };
  }

  handleChange = data => event => {
    this.setState({ [data]: event.target.value });
    //removes error message if they start editing something
    this.setState({ error: "" });
  };

  clickedSubmit = event => {
    event.preventDefault();

    const { firstName, lastName, username, email, password } = this.state;
    const user = {
      name: {
        firstName,
        lastName
      },
      username,
      email,
      password,
      open: false
    };
    console.log(user);
    //send data to signup method
    // signup(user).then(data => {
    //   //if the data contains an error, set the state
    //   if (data.error) {
    //     this.setState({ error: data.error });
    //   } else {
    //     this.setState({
    //       firstName: "",
    //       lastName: "",
    //       username: "",
    //       email: "",
    //       password: "",
    //       error: "",
    //       open: true
    //     });
    //   }
    // });
  };

  init = userId => {
    const token = isAuthenticated().token;
    read(userId, token).then(data => {
      if (data.error) {
        this.setState({ redirectToSignin: true });
        console.log("Error.");
      } else {
        this.setState({
          id: data._id,
          firstName: data.name.firstName,
          lastName: data.name.lastName,
          username: data.username,
          email: data.email
        });
      }
    });
  };
  componentDidMount() {
    const userId = this.props.match.params.userId;
    this.init(userId);
  }
  signupForm = (firstName, lastName, username, email, password) => {
    <form>
      <div className="form-group">
        <label className="text-muted">First name</label>
        <input
          onChange={this.handleChange("firstName")}
          type="text"
          className="form-control"
          value={firstName}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">last name</label>
        <input
          onChange={this.handleChange("lastName")}
          type="text"
          className="form-control"
          value={lastName}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">username</label>
        <input
          onChange={this.handleChange("username")}
          type="text"
          className="form-control"
          value={username}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">email</label>
        <input
          onChange={this.handleChange("email")}
          type="text"
          className="form-control"
          value={email}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">password</label>
        <input
          onChange={this.handleChange("password")}
          type="password"
          className="form-control"
          value={password}
        />
      </div>

      <button
        onClick={this.clickedSubmit}
        className="btn btn-raised btn-primary"
      >
        submit
      </button>
    </form>;
  };

  render() {
    const { firstName, lastName, username, email } = this.state;
    return (
      <div className="container mt-5 mb-5">
        <h2>Edit profile</h2>
        {this.signupForm(firstName, lastName, username, email, password)}
      </div>
    );
  }
}
export default EditProfile;
