import React, { Component } from "react";
import { isAuthenticated } from "../auth";
import { read, update } from "./apiUser";
import { Redirect } from "react-router-dom";

class EditProfile extends Component {
  constructor() {
    super();
    this.state = {
      id: null,
      firstName: null,
      lastName: null,
      email: null,
      username: null,
      password: null,
      redirectToProfile: false,
      error: ""
    };
  }

  handleChange = data => event => {
    this.setState({ [data]: event.target.value });
    //removes error message if they start editing something
    this.setState({ error: "" });
  };

  clickedSubmit = event => {
    event.preventDefault();

    if (this.isValid()) {
      const { firstName, lastName, username, email, password } = this.state;
      const user = {
        name: {
          firstName,
          lastName
        },
        username,
        email,
        password: password || undefined,
        open: false
      };
    }
    //send request to API
    const userId = this.props.match.params.userId;
    const token = isAuthenticated().token;

    update(userId, token, user).then(data => {
      //   if the data contains an error, set the state
      if (data.error) {
        this.setState({ error: data.error });
      } else {
        this.setState({
          redirectToProfile: true
        });
      }
    });
  };

  init = userId => {
    const token = isAuthenticated().token;
    read(userId, token).then(data => {
      if (data.error) {
        this.setState({ redirectToProfile: true });
        console.log("Error in updating profile.");
      } else {
        this.setState({
          id: data._id,
          firstName: data.name.firstName,
          lastName: data.name.lastName,
          username: data.username,
          email: data.email
        });
      }
    });
  };
  componentDidMount() {
    const userId = this.props.match.params.userId;
    this.init(userId);
  }

  isValid = () => {
    const { firstName, lastName, username, email, password } = this.state;

    if (firstName.length === 0) {
      this.setState({ error: "Name is required." });
      return false;
    }
    if (lastName.length === 0) {
      this.setState({ error: "Last name is required." });
      return false;
    }
    if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(email)) {
      this.setState({
        error: "A valid Email is required"
      });
      return false;
    }
    if (username.length === 0) {
      this.setState({ error: "username is required." });
      return false;
    }
    if (
      /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/.test(
        password
      )
    ) {
      this.setState({
        error:
          "Please enter a password with a minimum of eight characters, at least one letter, one number and one special character."
      });
      return false;
    }

    return true;
  };

  signupForm = (firstName, lastName, username, email, password) => (
    <form>
      <div className="form-group">
        <label className="text-muted">First name</label>
        <input
          onChange={this.handleChange("firstName")}
          type="text"
          className="form-control"
          value={firstName}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">last name</label>
        <input
          onChange={this.handleChange("lastName")}
          type="text"
          className="form-control"
          value={lastName}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">username</label>
        <input
          onChange={this.handleChange("username")}
          type="text"
          className="form-control"
          value={username}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">email</label>
        <input
          onChange={this.handleChange("email")}
          type="text"
          className="form-control"
          value={email}
        />
      </div>
      <div className="form-group">
        <label className="text-muted">password</label>
        <input
          onChange={this.handleChange("password")}
          type="password"
          className="form-control"
          value={password}
        />
      </div>

      <button
        onClick={this.clickedSubmit}
        className="btn btn-raised btn-primary"
      >
        update
      </button>
    </form>
  );

  render() {
    const {
      id,
      firstName,
      lastName,
      username,
      email,
      password,
      redirectToProfile,
      error
    } = this.state;

    if (redirectToProfile) {
      return <Redirect to={`/user/${id}`} />;
    }

    return (
      <div className="container mt-5 mb-5">
        <h2>Edit profile</h2>
        <div
          className="alert alert-danger"
          style={{ display: error ? "" : "none" }}
        >
          {error}
        </div>
        {this.signupForm(firstName, lastName, username, email, password)}
      </div>
    );
  }
}
export default EditProfile;
