import React, { Component } from "react";
import MainRouter from "../MainRouter";
import { Redirect } from "react-router-dom";

class Signin extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      error: "",
      redirectToReferer: false
    };
  }
  handleChange = data => event => {
    this.setState({ [data]: event.target.value });
    //removes error message if they start editing something
    this.setState({ error: "" });
  };

  authenticate(jwt, next) {
    if (typeof window !== "undefined") {
      localStorage.setItem("jwt", JSON.stringify(jwt));
      next();
    }
  }

  clickedSubmit = event => {
    event.preventDefault();

    const { email, password } = this.state;
    const user = {
      email,
      password
    };
    //send data to signup method
    this.signup(user).then(data => {
      //if the data contains an error, set the state
      if (data.error) {
        this.setState({ error: data.error });
      } else {
        //authenticate and redirect
        this.authenticate(data, () => {
          this.setState({ redirectToReferer: true });
        });
      }
    });
  };

  signup = user => {
    return fetch("http://localhost:8080/signin", {
      method: "POST",
      headers: {
        Accept: "aplication/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    })
      .then(response => {
        return response.json();
      })
      .catch(err => console.log(err));
  };

  render() {
    const { email, password, error, redirectToReferer } = this.state;

    if (redirectToReferer) {
      return <Redirect to="/" />;
    }
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Signin</h2>
        <div
          className="alert alert-danger"
          style={{ display: error ? "" : "none" }}
        >
          {error}
        </div>
        <form>
          <div className="form-group">
            <label className="text-muted">email</label>
            <input
              onChange={this.handleChange("email")}
              type="text"
              className="form-control"
              value={email}
            />
          </div>
          <div className="form-group">
            <label className="text-muted">password</label>
            <input
              onChange={this.handleChange("password")}
              type="password"
              className="form-control"
              value={password}
            />
          </div>
          <button
            onClick={this.clickedSubmit}
            className="btn btn-raised btn-primary"
          >
            submit
          </button>
        </form>
      </div>
    );
  }
}

export default Signin;
