import React, { Component } from "react";
import { isAuthenticated } from "../auth";
import { Redirect, Link } from "react-router-dom";
import { read } from "./apiUser";

const userIdGlobal = this.state.user ? this.state.user._id : "loading";

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: null,
      redirectToSignin: false
    };
  }

  init = userId => {
    const token = isAuthenticated().token;

    read(userId, token).then(data => {
      if (data.error) {
        this.setState({ redirectToSignin: true });
        console.log("Error.");
      } else {
        this.setState({ user: data });
      }
    });
  };

  componentDidMount() {
    const userId = this.props.match.params.userId;
    this.init(userId);
  }

  render() {
    const redirectToSignin = this.state.redirectToSignin;
    if (redirectToSignin) {
      return <Redirect to="/signin" />;
    }
    return (
      <div className="container mt-5 mb-5">
        <div className="row">
          <div className="col-md-6">
            <p>this is the user profile, printing below is the username.</p>
            <h2>Handle: @{isAuthenticated().user.username}</h2>
            <h6>Email: {isAuthenticated().user.email}</h6>
            <p>
              {this.state.user ? this.state.user.name.firstName : "loading"}{" "}
              {this.state.user ? this.state.user.name.lastName : "loading"}{" "}
              {this.state.user ? this.state.user._id : "loading"}
            </p>
          </div>
          <div className="col-md-6">
            {isAuthenticated().user &&
              isAuthenticated().user._id == userIdGlobal && (
                <div className="d-inline-block mt-5 mb-5">
                  <Link
                    className="btn btn-raised btn-success mr-5"
                    to={`/user/edit/${this.state.user._id}`}
                  >
                    Edit Profile
                  </Link>
                  <Link
                    className="btn btn-raised btn-danger mr-5"
                    to={`/user/edit/${this.state.user._id}`}
                  >
                    Delete
                  </Link>
                </div>
              )}
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
